import React, { Component } from "react";
import { Fetch } from "react-data-fetching";

import { Button, Card, Flex as FlexBase, Heading, Text } from "rebass";
import { borders, height, minHeight } from "styled-system";
import styled from "styled-components";

// import { Loader } from "./components";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
  ${height}
`;

export default class IssuesList extends Component {
  constructor(props) {
    super(props);
    this.resetRetch = this.resetRetch.bind(this);
  }
  resetRetch() {
    this.props.refresh();
  }
  render() {
    let { project, refetch } = this.props;
    return (
      <Fetch
        loader={() => (
          <Flex
            flexDirection="column"
            alignItems="center"
            justifyContent="center"
            height="auto"
          >
            Loading...
          </Flex>
        )} // Replace this with your lovely handcrafted loader
        url={`api/issues/${project}`}
        timeout={5000}
        refetchKey={refetch}
        onFetch={this.resetRetch}
      >
        {({ data }) => (
          <Flex flexDirection="column" mx="auto" width={1 / 2}>
            <h1>Username</h1>
            {data
              ? data.map(item => (
                  <Card my="2" bg="white">
                    <Heading>{item.item_title}</Heading>
                    <Text>{item.open}</Text>
                    <Text>{item.id}</Text>
                    <Text>{item.issue_title}</Text>
                    <Text>{item.issue_text}</Text>
                    <Text>{item.created_by}</Text>
                    <Text>{item.assigned_to}</Text>
                    <Text>{item.project}</Text>
                  </Card>
                ))
              : ""}
          </Flex>
        )}
      </Fetch>
    );
  }
}
