import React, { Component } from "react";
import { Box, Button, Flex as FlexBase } from "rebass";
import { borders, minHeight } from "styled-system";
import styled from "styled-components";

import { IssueForm } from "./IssueForm";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
`;

export class IssueContainer extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.state = {
      title: "",
      text: "",
      created_by: "",
      assigned_to: "",
      status_text: "",
      issues: []
    };
  }

  handleInputChange(event) {
    event.preventDefault();
    let { value, name } = event.target;
    this.setState({
      [name]: value
    });
  }
  handleFormSubmit(event) {
    event.preventDefault();
    let { value } = event.target;
    alert("form submitted: " + value);
  }
  render() {
    let notIssues = this.state;
    delete notIssues.issues;
    return (
      <Flex flexDirection="column">
        IssueContainer
        <IssueForm
          submit={this.handleFormSubmit}
          formState={notIssues}
          inputState={this.handleInputChange}
        />
        <pre>{JSON.stringify(this.state, null, 2)}</pre>
      </Flex>
    );
  }
}
