import React, { Fragment } from "react";
import { Button, Flex as FlexBase, Heading } from "rebass";
import { borders, minHeight } from "styled-system";
import styled from "styled-components";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
`;

export const IssueForm = ({ inputState, formState }) => (
  <Fragment>
    <Heading>Submit a New Issue</Heading>
    <input type="text" name="title" id="title" />
    <textarea type="multiline" name="title" id="title" />
    <Flex>
      <input
        value={formState.created_by}
        onChange={inputState}
        type="text"
        name="created_by"
        id="created_by"
      />
      <input type="text" name="assigned_to" id="assigned_to" />
      <input type="text" name="status_text" id="status_text" />
    </Flex>
    <Button>Submit Issue</Button>
  </Fragment>
);
