import React, { Component } from "react";
import { Box, Button, Flex as FlexBase, Text } from "rebass";
import styled from "styled-components";
import {
  color,
  fontSize,
  lineHeight,
  minHeight,
  space,
  width,
  height
} from "styled-system";

const Flex = styled(FlexBase)`
  ${minHeight}
`;

const Input = styled.input`
    ${color}
    ${fontSize}
    ${height}
    ${lineHeight}
    ${space}
    ${width}
`;

const initialState = {
  bookForm: {
    title: "hello"
  }
};

class PersonalLibrary extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      bookForm: { title: initialState.bookForm.title }
    };
  }
  handleChange(e) {
    e.preventDefault();
    let { value } = e.target;
    this.setState(prevState => {
      return {
        bookForm: { title: value }
      };
    });
  }
  render() {
    let {
      bookForm: { title }
    } = this.state;
    return (
      <Flex
        justifyContent="center"
        flexDirection="column"
        height="auto"
        minHeight="100vh"
        mx="auto"
        width={[1 / 2]}
        bg="white"
      >
        <Box>
          <Text pb={3} fontSize={[4, 4, 4]}>
            Personal Library
          </Text>
          <Flex flexDirection="column" width={1 / 5}>
            <label htmlFor="book-title">Book Title</label>
            <Input
              p={2}
              fontSize={2}
              placeholder="Input book title"
              value={title}
              onChange={this.handleChange}
              type="text"
              id="book-title"
              name="book-title"
            />
          </Flex>
          <Button bg="indigo">Submit New Book!</Button>
        </Box>
        <Box>Some stuff</Box>
      </Flex>
    );
  }
}

export default PersonalLibrary;
