import React, { Component } from "react";
import { Button, Flex, Text } from "rebass";
// import styled from "styled-components";
// import { space } from "styled-system";

const initialState = {
  bookForm: {
    title: "hello"
  }
};
class PersonalLibrary extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      bookForm: { title: initialState.bookForm.title }
    };
  }
  handleChange(e) {
    e.preventDefault();
    let { value } = e.target;
    this.setState(prevState => {
      return {
        bookForm: { title: value }
      };
    });
  }
  render() {
    let {
      bookForm: { title }
    } = this.state;
    return (
      <Flex flexDirection="column" mx="auto" width={[1 / 2]} bg="white">
        <Text pb={3} fontSize={[4, 4, 4]}>
          Personal Library
        </Text>
        <input
          value={title}
          onChange={this.handleChange}
          type="text"
          id="book-title"
          name="book-title"
        />{" "}
        <Button bg="indigo">Submit New Book!</Button>
      </Flex>
    );
  }
}

export default PersonalLibrary;
