import React, { Component } from "react";
import { Box, Button, Flex as FlexBase } from "rebass";
import { requestToApi } from "react-data-fetching";
import { borders, minHeight } from "styled-system";
import styled from "styled-components";

import { IssueForm } from "./IssueForm";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
`;

export class IssueContainer extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.state = {
      title: "",
      text: "",
      created_by: "",
      assigned_to: "",
      status_text: "",
      issues: [],
      response: {}
    };
  }

  handleInputChange(event) {
    event.preventDefault();
    let { value, name } = event.target;
    this.setState({
      [name]: value
    });
  }

  async handleFormSubmit(event) {
    event.preventDefault();
    let { value } = event.target;
    alert("form submitted: " + value);
    console.log("calling api/issues");
    let { title, text, created_by, assigned_to, status_text } = this.state;
    let formData = {
      title,
      text,
      created_by,
      assigned_to,
      status_text
    };
    const apiResponse = await requestToApi({
      url: `/api/issues`,
      method: "POST",
      body: formData,
      headers: { ["Cache-Control"]: "no-cache" },
      onTimeout: () => console.log("⏱️ Timeout!"),
      onProgress: progression => ("♻️ Progressing...", progression),
      // params: { page: 5, start: 0, limit: 20 },
      timeout: 2500
    });
    console.log(Object.keys(apiResponse));
    console.log(apiResponse.data);
    this.setState(() => ({ response: apiResponse.data }));
  }
  render() {
    let notIssues = this.state;
    delete notIssues.issues;
    return (
      <Flex flexDirection="column">
        IssueContainer
        <IssueForm
          submit={this.handleFormSubmit}
          formState={notIssues}
          inputState={this.handleInputChange}
        />
      </Flex>
    );
  }
}
