import React, { Component } from "react";
import { Fetch } from "react-data-fetching";

import { Button, Flex as FlexBase, Heading } from "rebass";
import { borders, minHeight } from "styled-system";
import styled from "styled-components";

// import { Loader } from "./components";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
`;

export default class IssuesList extends Component {
  constructor(props) {
    super(props);
    this.resetRetch = this.resetRetch.bind(this);
  }
  resetRetch() {
    this.props.refresh();
  }
  render() {
    let { project, refetch } = this.props;
    return (
      <Fetch
        // loader={<Loader />} // Replace this with your lovely handcrafted loader
        url={`api/issues/${project}`}
        timeout={5000}
        refetchKey={refetch}
        onFetch={this.resetRetch}
      >
        {({ data }) => (
          <Flex mx="auto" width={1 / 2}>
            <h1>Username</h1>
            <p>{JSON.stringify(data, null, 2)}</p>
          </Flex>
        )}
      </Fetch>
    );
  }
}
