import React, { Component } from "react";
import { Box, Button, Flex as FlexBase } from "rebass";
import { borders, minHeight } from "styled-system";
import styled from "styled-components";

import { IssueForm } from "./IssueForm";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
`;

export class IssueContainer extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.state = {
      issueForm: {
        title: "",
        text: "",
        created_by: "",
        assigned_to: "",
        status_text: ""
      }
    };
  }

  handleInputChange(event) {
    event.preventDefault();
    let { value, name } = event.target;
    this.setState(prevState => ({ [name]: prevState[name] + value }));
  }
  render() {
    return (
      <Flex flexDirection="column">
        IssueContainer
        <IssueForm
          formState={this.state.issueForm}
          inputState={this.handleInputChange}
        />
        {this.state.issueForm.created_by}
        <pre>{JSON.stringify(this.state, null, 2)}</pre>
      </Flex>
    );
  }
}
