import React, { Fragment } from "react";
import { Button, Flex as FlexBase, Heading } from "rebass";
import { borders, minHeight } from "styled-system";
import styled from "styled-components";

const Flex = styled(FlexBase)`
  ${borders}
  ${minHeight}
`;

export const IssueForm = ({ inputState, formState }) => (
  <Fragment>
    <Heading>Submit a New Issue</Heading>
    {JSON.stringify(formState, null, 2)}
    <input
      value={title}
      onChange={inputState}
      type="text"
      name="title"
      id="title"
    />
    <textarea value={text} onChange={inputState} name="text" id="text" />
    <Flex>
      <input
        value={created_by}
        onChange={inputState}
        type="text"
        name="created_by"
        id="created_by"
      />
      <input
        value={assigned_to}
        onChange={inputState}
        type="text"
        name="assigned_to"
        id="assigned_to"
      />
      <input
        value={status_text}
        onChange={inputState}
        type="text"
        name="status_text"
        id="status_text"
      />
    </Flex>
    <Button>Submit Issue</Button>
  </Fragment>
);
